import React, {Component} from 'react';
import Request from '../../helpers/request';

class PirateForm extends Component{
  constructor(props){
    super(props);
    this.state = {
      ships: [],
      pirate: {
        firstName: "",
        lastName: "",
        age: 0,
        ship: null
      }
    }
    this.handleChange = this.handleChange.bind(this);
  }

  componentDidMount(){
      const request = new Request()
        request.get('/api/ships')
        .then((data) => {
          this.setState({ships: data})
        })
  }

  handleChange(event){
    let propertyName = event.target.name;
    let pirate = this.state.pirate
    pirate[propertyName] = event.target.value
    this.setState({pirate: pirate})
  }


  render(){
    if(this.state.ships === 0){
      return <p>Loading...</p>
    }
    return (
      <div>
        <form>
          <input type="text" placeholder="Fisrt Name" name ="firstName" onChange={this.handleChange} value={this.state.pirate.firstName} />
        </form>
      </div>
    )
  }
}


export default PirateForm;
